import { Bytes } from '@oasislabs/types';
import { Db } from '@oasislabs/common';
import Service from '../service';
import { Idl } from '../idl';
import { OasisGateway, RpcOptions } from '../oasis-gateway';
import { DeployHeaderOptions } from './header';
import { RpcCoder } from '../coder';
/**
 * deploy creates a service on the Oasis cloud.
 *
 * @returns a Service object to make rpc requests with.
 */
export default function deploy(options: DeployOptions): Promise<Service>;
/**
 * DeployOptions specify the arguments for deploying a Service.
 */
declare type DeployOptions = {
    bytecode: Bytes;
    idl?: Idl;
    arguments?: Array<any>;
    header?: DeployHeaderOptions;
    gateway?: OasisGateway;
    db?: Db;
    coder?: RpcCoder;
    options?: RpcOptions;
};
export {};
